mapper.select:
select *,(select max(reviewtime) from postcomment p where p.postId=po.postId)lasttime from post po where fmid in (select fmid from forummanagement where pid=#{pid})
and title like '%${title}%'
and fmid = #{forumid}
and essence = #{essence}
order by solid desc,releasetime desc
order by solid desc,essence desc,lasttime desc,releasetime desc

select *,(select max(reviewtime) from postcomment p where p.postId=po.postId)lasttime
from post po
where postId = #{postId}

select * from postcomment 
where postId = #{postId}
order by reviewtime desc

SELECT *,(SELECT MAX(reviewtime) FROM postcomment p WHERE p.postId=po.postId)lasttime FROM post po WHERE userId = #{userId}
and title like '%${title}%'
ORDER BY releasetime DESC
