mapper.select:
		
		 SELECT * FROM GoldnoTes 
		 
		     
		          and userId=#{userId}
		     
		     
		          and acquisitionMode=#{acquisitionMode}
		     
		 
ORDER BY `recordDate` DESC

mapper.insert:
INSERT INTO putforWard(`userID`,`putname`,`bankID`,`bankAccount`, `openBankName`,`submitTime`,`auditTime`,`money`,`auditStatus`)VALUES (#{putforWard.userid},#{putforWard.putname},#{putforWard.bankid},#{putforWard.bankaccount},#{putforWard.openbankname}, #{putforWard.submittime,jdbcType=DATE},NULL,#{putforWard.money},1);
INSERT INTO putforwardRecord(userID,`bankAccount`,`bankID`,`submitTime`,`completeTime`,`money`,`adminOpinion`) VALUES(#{putforWard.userid},#{putforWard.bankaccount},#{putforWard.bankid},#{putforWard.submittime,jdbcType=DATE},NULL,#{putforWard.money},NULL);
INSERT INTO goldNotes(userID,recordDate,recordDescribe,recordInAndOut,auditStatus) VALUES(#{putforWard.userid},#{putforWard.submittime,jdbcType=DATE},'金币提现',-#{putforWard.money},4)

mapper.select:
		SELECT s.serviceTitle,s.serviceCoverImg,e.serviceAppraiseContent,e.serviceAppraiseDate FROM evaluationservice e JOIN services s ON e.serviceId=s.serviceId 
WHERE e.userid=#{evaluationservice.userid}
ORDER BY e.serviceAppraiseDate DESC
